import React, {useState} from 'react';
import './App.css'

function App() {
  const [quadrados, setQuadrados] = useState ([])
  const [color, setColor] = useState("red")

  function createDiv(){
    setQuadrados([...quadrados, 'qualquercoisa'])
  }

  function getRandomColor() {
    var letters = "0123456789ABCDEF";
    var color = "#";
    for (var i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
   }
   //var newColor = getRandomColor(); // #E943F0

   function handleHover(){
     console.log("handleHover")
     setColor
   }


  return (
    <div className="App">
        <button onClick={createDiv}>Adicionar</button>    

        {
          quadrados.map(elemento =>   {
          return (<div onMouseEnter={handleHover} style={{backgroundColor: color}} className="estiloQuadrado">Oi Rob</div>)
          })
        }



    </div>
  );
}

export default App;
