import React, {useState} from 'react';
//import './App.css'
import styled , { css } from 'styled-components'

const DivLoca = styled.div`
    background-color: props.parameter;
    height: 100px;
    width: 100px;
    border: 2px solid #000000;
`



function App() {
  const [quadrados, setQuadrados] = useState ([{color: 'blue'}])
  const [color, setColor] = useState("red")

  function createDiv(){
    setQuadrados([...quadrados, {color:'blue'}])
  } 

  function getRandomColor() {
    var letters = "0123456789ABCDEF";
    var color = "#";
    for (var i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
   }
   //var newColor = getRandomColor(); // #E943F0

   function handleHover(){
     console.log("handleHover")
     console.log(index)
     setColor(getRandomColor())
   }

  function handleLeave (){
    console.log("handleLeave")
    setColor('red')
  }

  return (
    <div className="App">
        <button onClick={createDiv}>Adicionar</button>    

        {
          quadrados.map(elemento =>   {
          return (
            <DivLoca
            onMouseEnter={handleHover} 
            onMouseLeave={handleLeave}
            style={{backgroundColor: color}}
            >
                    Oi Rob

            </DivLoca>)
          })
        }



    </div>
  );
}

export default App;
